#define rect_center    vec2(250, 250)
#define rect_size      vec2(200, 200)
#define rect_radius    16.0
#define rect_color     vec4(1, 0.85, 0, 0)

float roundedRect(vec2 pos)
{   
    pos     			  		/= iResolution.y;
    vec2 size = rect_size 		/ iResolution.y;
    float radius = rect_radius 	/ iResolution.y;
    return 
        smoothstep(size.x / 2.0 + radius, size.x / 2.0 - radius, abs(pos.x)) * 
        smoothstep(size.y / 2.0 + radius, size.y / 2.0 - radius, abs(pos.y));    
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
    vec2 pos = fragCoord - rect_center;
    float delta = 0.5 / rect_radius;
    fragColor = rect_color * smoothstep(0.5 - delta, 0.5 + delta, roundedRect(pos));
}
