#define JULIA
#define SMOOTHFRACT

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.y /= iResolution.x / iResolution.y;


    vec2 puv;
    puv.x = atan(uv.y / uv.x);
    puv.y = length(uv);

    uv = puv;


    vec2 fuv = uv;

    #ifndef JULIA
    fuv.x -= 0.25;
    #endif

    fuv *= 1.5;

    #ifdef JULIA
    vec2 z = fuv;
    vec2 c = vec2(sin(iTime * 0.5) * 0.8, cos((iTime * 0.1 + sin(iTime * 0.21)) * 0.5));
    #else
    vec2 z = vec2(0);
    vec2 c = fuv;
    #endif
    float i;
    float zdz;
    for (i = 0.0; i < 20.0; i++)
    {
		z= vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        zdz = dot(z, z);
        if (zdz > 4.0)
            break;
    }

    #ifdef SMOOTHFRACT
    float log2=0.69314718056;
    float L = log(zdz)/2.0;
    float nu = log(L/log2)/log2;
    i+=1.0-nu;
    #endif

    float dist = float(i) / 20.0;

    fragColor = vec4(dist);
}
