void DrawTree( out vec4 fragColor, in vec2 fragCoord )
{

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;

    vec2 positionInScene = vec2(0.4, 0.6);
    vec2 basePos = p - positionInScene;

    float wind = cos(iTime*1.0)*0.064;
    vec2 windPos = p - vec2(positionInScene.x+wind, positionInScene.y);

	vec2 q;

    float MovedCos = (1.0+(cos(iTime*1.0))/2.0);
    float r = 0.0;
    // Time varying pixel color
    vec3 col = mix( vec3( 1.0, 0.3, 0.0 ), vec3( 1.0, 0.8, 0.3 ), sqrt(p.y+MovedCos*0.1)); 

    //Ground and wood
    q = basePos;
    r = 0.015;
    r += 0.002*cos(120.0*q.y);
    r += exp(-60.0*p.y);
    float ErasePoint = 0.00;

    float qy = (windPos + sqrt(p.y)*(basePos-windPos)).y+0.5;
    col *= 1.0 - (1.0-smoothstep(r, r+0.0005, abs(q.x-wind*qy*2.0) )) * (1.0-smoothstep(ErasePoint, ErasePoint+0.01, q.y));


    //leaves 
	q = windPos;
    float wings = 10.0;
    r = 0.2 + 0.1*cos(atan(q.y, q.x)*wings + 16.0 * q.x + (cos(iTime*5.0))/2.0 * 0.5);
    col *= smoothstep( r, r+MovedCos*0.01+0.00, length(q));





    basePos = p - vec2(0.8, 0.4); 
    windPos = p - vec2(0.8+wind, 0.4);

    //Ground and wood
    q = basePos;
    r = 0.01;
    float trunkComplexity = 0.0015;
    r += trunkComplexity*cos(120.0*q.y);
    r += exp(-60.0*p.y);
    ErasePoint = 0.00;

    qy = (windPos + sqrt(p.y)*(basePos-windPos)).y+0.35;
    col *= 1.0 - (1.0-smoothstep(r, r+0.0005, abs(q.x-wind*qy*3.0) )) * (1.0-smoothstep(ErasePoint, ErasePoint+0.01, q.y));


    //leaves 
	q = windPos;
    wings = 7.0;
    r = 0.1 + 0.05*cos(atan(q.y, q.x)*wings + 16.0 * q.x + (cos(iTime*5.0))/2.0 * 0.5);
    col *= smoothstep( r, r+MovedCos*0.01+0.00, length(q));

    // Output to screen
    fragColor = vec4(col,1.0);
}
