void mainImage( out vec4 O, vec2 U )
{  
    vec2 R = iResolution.xy;
    float x = U.x-.5*R.x;

    if (x==.5) { O=vec4(1,0,0,0); return; } // red separator

    int z = int(8.*U.y/R.y);
    O = x > 0.
          ? texelFetch( iChannel0, ivec2(U)%(1024>>z), z)  // bluenoise
          : texelFetch( iChannel1, ivec2(U)%(256>>z) , z); // whitenoise

    if (abs(x/R.x) < .25) O = O.xxxx;      // center strip : B&W
    O = .5 + (O-.5)*exp2(4.*iMouse.y/R.y); // mouse.y boost intensity
 }
