const float r = 2.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 colIn = texelFetch(iChannel0, ivec2(fragCoord), 0);
    fragColor = colIn;
}

const float r = 2.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.yy;
    vec2 uv3 = 2.0*uv;

	int it, maxIt;
    float x, y, x0, y0, xTemp;

    vec2 c = texelFetch(iChannel1, ivec2(fragCoord), 0).xy;

   	x = uv3.x;
    y = uv3.y;
   	it = 0;
    maxIt = 1000;
    while (x*x + y*y < r*2.0 && it < maxIt) {
    	xTemp = x*x - y*y;
        y = 2.0*x*y + c.y;
        x = xTemp + c.x;
        ++it;
    }

    float a = float(it)*0.001;

    // Output to screen
    fragColor = vec4(20.0*pow(a,1.0), 32.0*pow(a,1.5), 2.0*pow(a,0.5) ,1.0);
    fragColor = clamp(fragColor, 0.0, 1.0);
    vec4 pCol = texelFetch(iChannel0, ivec2(fragCoord), 0);
    vec4 nCol =
        (texelFetch(iChannel0, ivec2(fragCoord)+ivec2(1, 0), 0) +
        texelFetch(iChannel0, ivec2(fragCoord)+ivec2(0, 1), 0) +
        texelFetch(iChannel0, ivec2(fragCoord)+ivec2(-1, 0), 0) +
        texelFetch(iChannel0, ivec2(fragCoord)+ivec2(0, -1), 0))*0.25;
    pCol = 0.05*pCol + 0.95*nCol;
    fragColor = pCol*0.95+fragColor*0.05;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime*0.5;
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.xy;
    vec2 c = vec2(cos(t*0.72132753)*0.5, sin(t*1.5234652)*0.5);
    vec2 cn = normalize(c);
    c = 2.0*cn + 1.0*c;

    vec2 cp = texelFetch(iChannel1, ivec2(fragCoord), 0).xy;

    float d = length(uv);

    fragColor = vec4(cp*(0.5+0.5*d) + c*(0.5-0.5*d), 0.0, 0.0);
}
