/*
https://github.com/matthewroberthenderson/Texter
part of getting the texture processing in GL, 
no longer using imagemagik and LunaPic to process images

*/
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float TAU = 6.28318530718;
float power = 4.0;
float lightIntensity = 1.0;

    vec2 uv = fragCoord/iResolution.xy;
    float aRatio = iResolution.x/iResolution.y;
    uv.x *= aRatio;

    vec4 TextureSample = texture(iChannel0, uv);
    float f0 = TextureSample.x;

    float ao = 1.0 + 
        (1.0 / TAU * pow(TextureSample.x,2.0)) * exp(-((uv.x*uv.x) + (uv.y*uv.y)) / 2.0 * pow(TextureSample.x,2.0))
        - TextureSample.x;


    ao *= 1.0 + 

       (1.0 / TAU * pow(TextureSample.y,2.0)) * exp(-((uv.x*uv.x) + (uv.y*uv.y)) / 2.0 * pow(TextureSample.y,2.0))
        - TextureSample.y;






    ao *= 1.0 + 

        (1.0 / TAU * pow(TextureSample.z,2.0)) * exp(-((uv.x*uv.x) + (uv.y*uv.y)) / 2.0 * pow(TextureSample.z,2.0))

        - TextureSample.z;


    ao = clamp(ao, -1.0, 1.0);
   	ao = pow(ao, power) * lightIntensity;


    	fragColor = (1.0-vec4(ao,ao,ao,ao));

}
