void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float dist = 7.0f * length(fragCoord.xy - iResolution.xy)/length(iResolution.xy);
    float red = abs(cos(iTime - dist));
    //float red = 0.0f;
    dist = 7.0f * length(fragCoord.xy)/length(iResolution.xy);
    float green = abs(cos(iTime - dist));
	//float green = 0.0f;
    fragColor = vec4(red, green, 0.0f, 1.0f);
}
