#define t iTime
#define r iResolution

//Based on https://twitter.com/yosshin4004/status/1251357672504360966

void mainImage( out vec4 o, in vec2 U )
{
    vec3 d=vec3(U/r.xy-.5,.5),p=vec3(0,6,t),q;
    for(int i=0;i<99;i++)
    {
        p+=d*(length(sin(p.zxy*.6)-cos(p.xyz))-.5);
        if(i==94)q=p+=d=vec3(.6);
    }
    ivec3 u=ivec3(q*5e2);
    o+=vec4(u.y&150,u.x&99,u.z&99,0)/1e3*length(p-q)+(p.z-t)*.03;
}
