#define GENTLE 1 // set to 1 for a more plausible photo setting

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

     vec3 col = texture(iChannel0, uv).rgb;

    mat3 blowout = mat3(1.87583893, 0.96308725, 0.,
       0.96308725, 1.17416107, 0.,
       0.        , 0.        , 0.5);
    vec3 cent = vec3(0.47968451, 
                     0.450743, 
                     0.45227517);



    vec3 dir = blowout * (col - cent);

    vec3 maxes = (step(vec3(0.0), dir) - col)/dir;

    float amount = min(maxes.x, min(maxes.y, maxes.z));

    float weight = 0.75;
    if (iMouse.x < fragCoord.x) {
	    amount = min(amount, 0.5);
    	weight = 1.0;
    }

    col = col + dir * weight * amount;

    // Output to screen
    fragColor = vec4(col,1.0);
}
