void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 newCoord = 2.0*vec2((fragCoord.xy - 0.5*iResolution.xy) / iResolution.y);

  vec3 backgroundColor = vec3(1.0);
  vec3 shapeColor = vec3(1.0, 0.0, 0.0);
  float radius = 0.6;

    if(length(newCoord) < radius) backgroundColor = shapeColor;

  fragColor = vec4(backgroundColor, 1.0);

}
