void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.0,0.0,0.0);
    float thickness = 2.0;
    float spacing = 20.0;
    if (mod(fragCoord.x,spacing) > spacing-thickness)
        {
            col = vec3(1.0,1.0,5.0)*sin((-iTime*10.0)+(fragCoord.x*0.06)+(fragCoord.y*0.02));
        }
    fragColor = vec4(col,1.0);
}
