void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 f = normalize(vec3(fragCoord - iResolution.xy/2.,iResolution.x) );
    f = vec3(f.x*cos(iTime)-sin(iTime)*f.z,f.y,f.x*sin(iTime)+cos(iTime)*f.z);
    vec3 ro = vec3(0.*cos(iTime)-sin(iTime)*-10.,f.y,0.*sin(iTime)+cos(iTime)*-10.);
    vec3 s = vec3(0,0,0);
    float r = 1.1;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    for (int x = 0; x < 1000; x++){

        if (length( ro - s) < 2.){
            //col = vec3(1,1,0);
            float t = dot(s-ro,f);
            vec3 p = ro +f*t;
            float y = length(s-p);

            if (y<r){
            float x = sqrt(r*r - y*y);
            float t1 = t-x;
            float t2 = t+x;
            col = f*t1+ro - s;
            f = f - 2.*dot(f,col)*col;
            //break;
            }
        }
        if (ro.y < -5.)
        {
            int v = (abs(int(ro.x))+int(ro.x>0.)+abs(int(ro.z))+int(ro.z>0.)) % (2);
            col = vec3(v,0,0);
            col = texture(iChannel0,vec2(ro.x,ro.z)/50.).rgb;
            break;
        }
        ro += f/10.;
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
