float dots( vec2 uv, float angle, vec2 center )
{
    float c = cos(angle);
    float s = sin(angle);

    uv -= center;

    uv = vec2( uv.x * s + uv.y * c, -uv.x * c + uv.y * s);
    uv *= mix( 500., 300., length(uv));
    //uv *= 300.;
    return pow(sin(uv.x)*sin(uv.y), .8)*2.;
    //return sin(uv.x)*sin(uv.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture( iChannel0, uv ).xyz;

    col.r *= dots(uv, 10., vec2(.5,.5)); 
    col.g *= dots( uv, 20., vec2(.5,.3));
    col.b *= dots( uv, 30., vec2(.3,.6));

// Output to screen
    //col = vec3(dots(uv, 10., vec2(.5,.5)));
    fragColor = vec4(col,1.0);
}
