// Gigatron France  ; use min command (c) 
// min(x,y) ; 
// khronos group def ;
// min returns the minimum of the two parameters.
//  (y < x) ? return y : return x  

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 tx0 = texture(iChannel0,uv).xyz;
	vec3 tx1 = texture(iChannel0,uv-vec2(0.005,0.0)).xyz;
    vec3 tx2 = texture(iChannel0,uv-vec2(0.005)).xyz;
    vec3 tx3 = texture(iChannel0,uv+vec2(0.005)).xyz;


  //  vec3 f0   = min(tx0,tx1);
  //  vec3 f1   = min(f0,tx2);
  //  vec3 f2   = min(f1,tx3);

  // min(min(min(min(  /// to dooo :!!!

    vec3 f3 = min(min(min(tx0,tx1),tx2),tx3);

    // Output to screen
    fragColor = vec4(f3,1.0);
}
