#define Sm(a, b, c) smoothstep(a, b, c)

vec4 drwEye(vec2 pix){
    vec4 col = vec4(0.3);
    return col;
}

vec4 drwMouth(vec2 pix){
    vec4 col = vec4(0.3);
    return col;
}

vec4 drwHead(vec2 pix){
    vec4 col = vec4(0.9, 0.65, 0.1, 1.0);
    float dst = length(pix);
    col.a = Sm( 0.4, 0.39, dst);
    return col;
}

vec4 drwClr(vec2 pix){
    vec4 col = vec4(1.0);

    vec4 head = drwHead(pix);
    col = mix(col, head, head.a);
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Draw area set up
    vec2 uv = fragCoord / iResolution.xy;
    uv.x = uv.x - 0.5;
    uv.y = uv.y - 0.5;
    uv.x = uv.x * (iResolution.x / iResolution.y);




    // Output to screen
    fragColor = drwClr(uv);
}
