vec2 bounce(float t, vec2 p){
    return abs(fract(t*p*0.1)-0.5)*4.-1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float c = sin(35.*length(uv-bounce(iTime*0.9, vec2(2.2,3.4))))*0.5+0.5;
    c += sin(35.*length(uv-bounce(5.+iTime*0.9, vec2(0.2,2.4))))*0.5+0.5;
    c += sin(35.*length(uv-bounce(30.+iTime*0.9, vec2(1.33,0.74))))*0.5+0.5;
    c*= 0.34;

    fragColor = vec4(0.,0.6*c,c,1.0);
}
