void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pi = radians(180.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    //float rad_mid = dot(uv,uv)

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*sin(iTime+(pi*uv.x) + vec3(255,255,255));
    vec3 x = 0.5 + 0.5*cos((uv.x*pi) + vec3(255,255,255));
    vec3 y = 0.5 + 0.5*cos((uv.y*pi) + vec3(255,255,255));

    // Output to screen
    fragColor = vec4(x*y,1.0);
}
