void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
	fragColor = texture(iChannel0, fragCoord.xy/iResolution.xy);
}

mat2 rot(float a) {
	float c = cos(a); float s = sin(a);
    return mat2(c, s, -s, c);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy - 0.5*iResolution.xy;
    uv *= 2.0/iResolution.y;
    uv *= 2.0*rot(iTime); 
    vec2 UV = fragCoord.xy - 0.5*iResolution.xy;
    UV *= rot(0.2*sin(iTime))*(2.0 + 0.06*sin(0.6*iTime));
    UV += 0.6*iResolution.xy;
    UV /= iResolution.xy;
    float s = abs(uv.x) + abs(uv.y); // "Square"
    fragColor = 1.05*texture(iChannel0, UV);
	fragColor += vec4(2.0)*( step(2.0, s) - step(2.025, s));
}
