float disk (vec2 r,vec2 center, float radius) {
    return 1.0 - smoothstep(radius-0.005, radius+0.005, length(r-center));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r = 2.0*vec2(fragCoord.xy-0.5*iResolution.xy)/iResolution.y;

    vec3 color_Red = vec3(1.0, 0.0, 0.0);
    vec3 color_Blue = vec3(0.0, 1.0, 0.0);
    vec3 color_Green = vec3(0.0, 0.0, 1.0);


    vec3 bg = vec3(0.0);
    float y=iTime*0.3;
    y = mod(y, 2.0)-1.0;
    float diskColor1 = disk(r,vec2(0.0,y), 0.5); 
    float diskColor2 = disk(r,vec2(y,y), 0.5);
    float diskColor3 = disk(r,vec2(-y,y), 0.5);
    vec3 ret1 = mix(bg, color_Red, diskColor1);
    vec3 ret2 = mix(bg, color_Blue, diskColor2);    
    vec3 ret3 = mix(bg, color_Green, diskColor3);


    vec3 pixel = ret1+ret2+ret3;
    fragColor = vec4(pixel,1.0);
}
