void mainImage(out vec4 fragColor, in vec2 fragCoord){
    fragColor = texelFetch(IMAGE, ivec2(fragCoord), 0);
}

/*
	[0,0] is previous mouse position
	[1,0] is current mouse position

	[3,0] is previous orbit position
	[4,0] is current orbit position
*/

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {    
    if (ivec2(fragCoord) == ivec2(0,0))
    	fragColor = texelFetch(STATE, ivec2(1, 0), 0);

    if (ivec2(fragCoord) == ivec2(1, 0))
        fragColor = vec4(ivec2(iMouse.xy), 0, 0);

    if (ivec2(fragCoord) == ivec2(3,0)) {
    	fragColor = texelFetch(STATE, ivec2(4, 0), 0);
    }

    if (ivec2(fragCoord) == ivec2(4, 0)) {
    	vec2 previous;
        if (MOUSE_CHANGE) 
            previous = vec2(0.0);
        else
        	previous = texelFetch(STATE, ivec2(3,0), 0).xy;

	    vec2 m = ZOOM*screen_coord(iMouse.xy, iResolution.xy);
        vec2 next = F(previous, m);
        if (length(next) > 5000.0) {
        	next = previous;
        }
        fragColor = vec4(next, 0.0, 0.0);
    }
}

#define STATE iChannel0
#define IMAGE iChannel1
#define ZOOM 20.0
#define MOUSE_PRESSED (int(iMouse.z) > 0 && int(iMouse.w) > 0)
#define MOUSE_CHANGE (ivec2(texelFetch(STATE, ivec2(0,0), 0).xy) != ivec2(texelFetch(STATE, ivec2(1,0), 0).xy))
#define RADIUS 0.01

vec2 dual_pow(vec2 d, float p) {
    // implement d^p = (a+bÎµ)^p
    float r = pow(d.x, p);
    float r1 = pow(d.x, p - 1.0);
	return vec2(r, d.y * p * r1);    
}

vec2 cmul(vec2 c1, vec2 c2) {
    // complex multiplication
    return vec2(c1.x*c2.x - c1.y*c2.y, 
                c1.x*c2.y + c1.y*c2.x);
}

vec2 cpow(vec2 c, float p) {
	// implement c^p
    float r = length(c);
    float a = atan(c.y, c.x);
    return vec2(pow(r, p), a * p);
}


vec2 F(vec2 z, vec2 c) {
	return dual_pow(z, 2.0) + c;
    //return cmul(z,z) + c;
}

vec2 screen_coord(vec2 xy, vec2 dim) {
    return (xy - 0.5*dim) / min(dim.x, dim.y);
}

float sd_line(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length(pa - ba*h);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = ZOOM*screen_coord(fragCoord.xy, iResolution.xy);

    vec2 previous_orbit = texelFetch(STATE, ivec2(3,0), 0).xy;
    vec2 current_orbit = texelFetch(STATE, ivec2(4,0), 0).xy;

    float dist = sd_line(uv, current_orbit, previous_orbit); 
    //float dist = length(current_orbit - uv);

    float current_shade = float(dist < RADIUS);

    float shade;

    if (MOUSE_CHANGE) {
    	shade = current_shade;
    } else {
    	float previous_shade = texelFetch(IMAGE, ivec2(fragCoord), 0).x;
        shade = previous_shade + current_shade;
    }

	fragColor = vec4(vec3(shade), 1.0);
}
