void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float size = 50.;
	float mx = max(iResolution.x, iResolution.y);
	vec2 uv = fragCoord.xy / mx;
	vec2 center = iResolution.xy / mx * 0.5;
	float dist = length(center - round(uv * size) / size);
	float brightness = 1. - round(dist * size) / (size / (sin(iTime * 20.) / 10. + 3.));
	fragColor = vec4(brightness, brightness, brightness, 1);
}
