void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float r = 0.;
    float g = 0.;
    float b = 0.;

    float x = uv.x;
    float y = uv.y;

    float temp = 1.;
    temp -= mod(iTime,1.);

    r = (x+y)/2.;
    g = exp(-(1./x));
    if(mod(iTime,2.)>1.) b = mod(iTime,1.);else b = temp;


    // Output to screen
    fragColor = vec4(r,g,b,1.0);
}
