// sin function graphic

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set uv 
    vec2 uv = fragCoord/iResolution.xy;
    // set the resolution by double uv move with 1.0
    vec2 resolution = uv*2.0 - 1.0;
   	resolution.x *= iResolution.x / iResolution.y;

    // set coordonate x and y by resolution
    float x = resolution.x * 2.0;
    float y = resolution.y;
    // y = sin(x) 
    // need a absolute referenced value y - sin(x) because I used resolution var
    float ref = abs(y - sin(x));
    // create color output function var 
    vec3 color = ref * vec3(0.0, 0.0, 76.0);  
    // show the function using the vec4 
    fragColor = vec4( color,1.0);
}
