void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
	uv.x *= iResolution.x/iResolution.y;


    vec3 color_white = vec3(1,1,1);

    vec3 color_red = vec3(1,0,0);

    float pct = distance(uv,vec2(0.89, 0.5));

    float inner = 1.0 - step(0.13, pct);

    float middle = 1.0 - step(0.2825, pct);

    float outter = 1.0 - step(0.4, pct);

    float target_shape = (outter - middle + inner);

    float target_shape_inverse = 1.0 - target_shape;

    vec3 target_A =
    mix(color_white, color_red, target_shape);

    vec3 target_B =
    mix(color_white, color_red, target_shape_inverse);

    vec3 color = mix(target_A, target_B, sin(iTime));

	  fragColor = vec4(color, 1.0);
}
