#define TAU radians(360.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 	tex = texture(iChannel0, fragCoord / iResolution.xy);
	vec2	p = (2. * fragCoord - iResolution.xy) / iResolution.y;
    float	angle = atan(p.y, p.x);
    float 	l = length(p) * 20.;
    float	circleId = floor(l);
    float	modCircleId = mod(circleId, 2.);
    float 	d = tex.x;
    float speed = mix(0.1, -0.1, modCircleId);
    l+= sin((angle - iTime * speed) * circleId * 5.) * 0.3 * d;
    l = fract(l) * 2.;
    float	t = min(l, 2. - l);
    t = smoothstep((100. + 100. * tex.y) / iResolution.y, 0., abs(t-1.)); 
    fragColor = vec4(vec3(pow(t,1./2.2)),1.);
}

const	vec3	ck = vec3(13., 163., 38.) / 255.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 c = texture(iChannel0, fragCoord / iResolution.xy).xyz;
    vec3 d = c - ck;
    float m = smoothstep(0., 0.01, dot(d, d));    
    fragColor = vec4(vec3(m,dot(c, vec3(1)) * m, 0.),1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 acc = vec4(0);
    for(int i = -10; i <= 10; i ++)
    {
		 acc += texture(iChannel0, fragCoord / iResolution.xy + vec2(float(i), 0.) / iResolution.x);
    }
    fragColor = acc / float(10 * 2 + 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 acc = vec4(0);
    for(int i = -10; i <= 10; i ++)
    {
		 acc += texture(iChannel0, fragCoord / iResolution.xy + vec2(0., float(i)) / iResolution.y);
    }
    fragColor = acc / float(10 * 2 + 1);
}
