void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = ((fragCoord - 0.5 * iResolution.xy) / iResolution.y) * 2.5, z = vec2(0.0);
    float i = 0.0;
    for(; i < 100.0; i++){
        float a = z.x * z.x, b = z.y * z.y;
    	if(a + b >= 16.0) break;
        z = vec2(a - b, 2.0 * z.x * z.y) + uv;
    }
    fragColor = vec4(vec3(i / 100.0), 1.0);
}
