void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = texture(iChannel0, uv);

    int l = iFrame;

    int r = l >> 4 & 0xf;
    l = l & 0xf;
    for (int i = 0; i < 8; i++) {
        int nl = r;
        int F =(((r * 13) + (r >> 2) + 7 * 7) ^ r) & 0xf;
        r = l ^ F;
        l = nl;
    }
    l = ((r<<4)|l) & 0xff;

    ivec2 coords = ivec2(fragCoord / (iResolution.xy / 16.));

    if (coords.x + coords.y * 16 == l)
        fragColor = vec4(0, 1, 0, 0.5);

    if (iFrame % 260 == 0)
        fragColor = vec4(0);
}
