bool ground (vec3 p)
{
    if(p.y< sin(p.x) * cos(p.z))
        return true;
    return false;
}

vec3 trace (vec3 s, vec3 d)
{
    vec3 p = s;
    for(int i = 0; i<1000; i++)
    {
        p += d/200.;
        if(ground(p))
        {
            vec2 texUV = p.xz;
            return vec3(texture(iChannel0, texUV));
        }
    }
    return vec3(texture(iChannel1, d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 yo = uv*2.-1.;
    vec3 camera = vec3(1.,1.,iTime);
    vec3 dir = vec3(yo, iMouse.x/iResolution.x*iMouse.y/iResolution.y);


    // Time varying pixel color
    vec3 col = vec3(trace(camera, dir));

    // Output to screen
    fragColor = vec4(col,1.0);
}
