// COMMON
#define t(uv) texture(ch, uv).xy
#define rnd(x) fract(9999.*sin(9999.*x))
#define fl(x) floor(x*10.)/10.
#define R (5.)
#define s(x) smoothstep(0.,5.,x)

#define F (.06 - sin(uv.y*4.)*.06*s(t))
#define K (.05 + sin(uv.y*4.)*.02*s(t))
//#define K (.06)
#define Da 1.*s(t)
#define Db .4*s(t)
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))


vec2 d2p(vec2 d){return vec2(atan(d.x,d.y),length(d));}
vec2 p2d(vec2 p){return p.y*vec2(sin(p.x),cos(p.x));}
vec2 snowflakeSymmetry(vec2 p, float rays){
	vec2 ar = d2p(p);
	float astep = 2. * 3.1415 / rays;
	float a = ar.x, r = ar.y;
	a = mod(a, astep);
	a -= astep / 2.;
	a = abs(a);
	p = p2d(vec2(a, r));	
	return p;
}

vec2 symmetry(vec2 uv, float t) {
    //float row = floor(uv.x*2.);
    //uv=fract(uv*2.)/2.;
    uv-=.5;
    uv=snowflakeSymmetry(uv, 1.5);
    uv*=rot(3.1415*2.);
    uv+=.5;
    //if(uv.y>.7)return vec2(0.);
    return uv;
}

vec4 reaction(vec2 FC, vec2 r, sampler2D ch, float t, vec4 m)
{
    vec2 uv=FC.xy/r,_,px=1./r,v=t(uv),dx,dy,d,delta,dir;

    // flow
    //uv=((uv-.5)*rot(.05*length(uv-.5))+.5);

    // 
    _=vec2(R+R*rnd(uv.x+.01*uv.y+t),0.);

    uv=symmetry(uv, t);

    dx=(-v+(t(uv+px*_   )+t(uv+px*-_   ))/2.)/2.;
    dy=(-v+(t(uv+px*_.yx)+t(uv+px*-_.yx))/2.)/2.;

    //dir=vec2(1.,0.)*rot(t/10.)*.5+.5;
    d=(dx+dy)/2.;

    delta.x=Da*d.x-v.x*v.y*v.y+F*(1.-v.x);
    delta.y=Db*d.y+v.x*v.y*v.y-(K+F)*v.y;
    vec4 o=vec4(v+delta,1.,1.);

    if(t<.1)o*=0.,o+=1.-step(.05,length(uv-.5));//o+=rnd(fl(uv.x)+.01*fl(uv.y));

    return o;
}
