// Created by Alex Kluchikov

#define float3 vec3
#define float2 vec2
#define float4 vec4

float3 MIX(float3 x, float3 y)
{
	return abs(x-y);
}    

float CV(float3 c, float2 uv)
{
    float size=640./iResolution.x*0.003;
    float l=clamp(size*(length(c.xy-uv)-c.z),0.,1.);
	return 1.-l;
}

void mainImage(out float4 O, in float2 I)
{
    O=float4(0,0,0,1);
    for(float i=0.;i<2600.;i+=13.)
    {

        float3 c=float3(1.0,1.0,1.0);
		O.rgb=MIX(O.rgb,c*CV(float3(
			iResolution.x*(1.+sin(iTime*0.52+(i-1400.)*1.35))*.5,
			iResolution.y*(1.+sin(iTime*0.73+(i-1200.)*1.61))*.5,
			0.0),I));
    }
    O.rgb=(1.-O.rgb)*1.01;
    O.rgb=pow(O.rgb,float3(42.,32.,12.));
}
