void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 dist = vec2(.5) - uv;
    float ang = atan(dist.y,dist.x);
    vec2 polar = vec2(sin(ang+iTime),cos(ang-iTime));
    float col = step(length(dist * dist *dist * dist/polar ),.001  );

    fragColor = vec4( iTime * col,col * ang * iTime,col*(iTime-ang),col*sin(ang+iTime));
}
