float RESOLUTION = 150.; // higher is tighter

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Preparatory work
    vec2 uv = fragCoord/iResolution.xx;

    // At this stage, uv is basically the coordinates in the sea plan

    // Generate grid
    float col = max(sin(uv.x* RESOLUTION), sin(uv.y* RESOLUTION));

    // Antialiasing and threshold
    col = smoothstep(0.9,1.,col);

    // Output to screen
    fragColor = vec4(vec3(col),1.0);
}
