void mainImage(out vec4 FragColor, in vec2 FragCoord){
    vec2 uv = FragCoord/iResolution.xy;
    uv -= 0.5; 
    uv.x *= iResolution.x/iResolution.y;

    //uv = uv*2. - 1.;

    float da = iTime  ;


    float ds = length(abs(uv) - 0.3 );

    float a = atan(abs(uv.x) , abs(uv.y) + 0.3 ) ;

    float d = smoothstep(0.5, 0.1, length(uv)); 
    d = atan(abs(uv.x), uv.x*uv.y)* dot(sin( abs(uv.x)) , sin(uv.x*uv.y));
    d = atan(a*10. + ds*10.) * atan(a * 10. + da) * asin(d + ds);

    float d2 = smoothstep(1., 0.11, length( uv + sin(ds*cos(d*sin(d+a) ) + ds*da )-tan(da + a-atan(uv)) ) ); 
    d2 -= asin(a/d*d2);

    float d3 = smoothstep(uv.x*uv.y , 1. , ds);
    vec3 col = vec3(1.);
    col = vec3( cos(d2 * d + d3)*atan(d2/d3) ); 

    col /= vec3(0.5, 0.2, 0.1);
    col *= vec3((sin(da)+1.)/2., 0.2, (sin(da)+1.)/2. );

    FragColor = vec4(col, 1.);
}
