void mainImage(out vec4 pxColor, in vec2 pxCoord)
{
    vec2 uv = pxCoord / iResolution.xy;

    vec4 texColor = vec4(1, 1, 1, 1);
    texColor.r = abs(sin(uv.x));
    texColor.g = abs(sin(uv.y));
    texColor.b = 0.0;

    texColor.r *= tan(texColor.r * iTime);
    texColor.g *= tan(texColor.g * iTime);
    texColor.b *= tan(texColor.b * iTime);

    pxColor = texColor;
}
