// CC0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime * .01;    
    vec2 np = fragCoord / iResolution.y - .835;
    float w = length(np) + .23;        
    vec4 a = texture(iChannel0, vec2(atan(np.x, np.y) * .004 / asin(np.x*0.2), .2 / w) + t) * w * 2.0;        
    fragColor.r = a.r * .3;
    fragColor.g = a.g * .7;
    fragColor.b = a.b * 8.0;
}
