#define t iTime
#define r iResolution

void mainImage( out vec4 o, in vec2 U )
{
    o=vec4(0);//Required on Safari
    vec3 u=vec3(2.*U.xy-r.xy,r.y)/3e2;
    u=normalize(vec3(u.xy,sqrt(max(u.z*u.z-dot(u.xy,u.xy)*2.,0.))));
    for(int i=0;i<2;i++)//for(int i=0;i<6;i++)
    {
        float l=length(u);
        u.xz+=sin(u.z+t*.2+l);
        u.y+=cos(u.x+t*.6);
        o+=cos(l+vec4(.3,.1,0,0));
    }
}
