// Playing with image deformation by velocity field (vx,vy) = Sum_i{ (sin(k_i*y),sin(k_i*x)) } where frequnecies k_i vary in time


vec2 flow(vec2 p){     
    const int n  = 8;
    float t      = iTime*0.01 + 3.36545588;
    vec2  dp     = vec2(0.);
    for(int i=0; i<n; i++ ){
        float fi = float(i);
        float k  = fi*(1.+0.25*sin(fi+t*(1.+0.1*fi)));
        dp      += sin(p.yx*k)/(0.1+k);
    }
    return dp*0.3;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 p = fragCoord/iResolution.x;
    p +=vec2(0.6,0.3);

    float dt = 0.05;
    float freq=15.0;
    for(int i=0;i<16;i++){ p += flow(p*freq)*dt; } 
    vec4 c = texture(iChannel0,p);
    fragColor = c;
}
