void mainImage(out vec4 O, vec2 C) {
    O=vec4(0);
	ivec2 p=ivec2(500.*C/iResolution.y);
    int x=p.x,y=p.y,T=int(iTime*30.),n,i;
    for(i=0;i++<9;)
        T++,
        n=T+abs(((x+y-T*7)|(x-y+T*5))^((x+y-T*12)&(x-y+T*15))^((x+y-T*5)|(x-y+T*8))),
        O.xyz+=vec3(1,.2,.1)*fwidth(step(300.,float(n%800)))*exp(-float(p.y)*.008);
    }
