float SinLerp(float min, float max, float t) {
 	return mix(min, max, sin(t) * 0.5 + 0.5);   
}


// tentaclez 2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float rep = 2.;
    float x = uv.x;
    float y = fract(uv.y * rep);

    vec3 col = vec3(0);

    float off = .5;
    float t = iTime;
    float t1 = t + y * off * 2.;
    float t2 = t + fract(1.-y*off+.5);

    float count1 = SinLerp(.0, 5., SinLerp(1., 5., t1));
    float count2 = SinLerp(.0, 5., SinLerp(1., 5., t2));

    float w = mix(1., 0., uv.x);
    float th = .5; //ceil(uv.y * rep) / rep;
    float th2 = th * 0.5;

    float pi2 = 3.14*2.;
    float s1 = sin(x * pi2 * count1)*.1;
    float s2 = sin((x-1.) * pi2 * count2)*.1;

    float w1 = s1 * w + mix(0.5 - th2, 0.49, uv.x * 1.2);
    float w2 = s1 * w + mix(0.5 + th2, 0.51, uv.x * 1.2);

    float w11 = s2 * (1.-w) + mix(.49, 0.5-th2, uv.x*1.2-.2);
    float w22 = s2 * (1.-w) + mix(.51, 0.5+th2, uv.x*1.2-.2);

    if (y > w1 && y < w2)
        col = vec3(1);

    if (y+.5 > w11 && y+.5 < w22)
        col = vec3(1,1,1);


    if (y-.5 > w11 && y-.5 < w22)
        col = vec3(1,1,1);

    // Output to screen
    fragColor = vec4(col,1.0);
}
