void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y = 1.0 - uv.y + 0.5;

    vec2 n = normalize(uv + uv - vec2(1.0));


	float amt = sin(iTime)*0.25 + 0.25;
    float feather = 0.01;
    float base = 0.5;


    float grad = smoothstep(base + amt, base + amt + feather, n.y);
    grad = smoothstep(0.0, feather*10.0, grad);
    fragColor = vec4(grad);
}
