float circleSdf(vec2 p, float r, vec2 st) {
  return length(p - st) - r;
}

// https://thebookofshaders.com/10/
float random (vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) *
        43758.5453123);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  const vec3 BG_COLOR = vec3(0.25, 0.15, 0.05);
  const vec3 FG_COLOR_1 = vec3(0.95, 0.45, 0.25);
  const vec3 FG_COLOR_0 = FG_COLOR_1 * 0.4;

  vec2 st = fragCoord.xy / iResolution.y;

  vec2 bucket = floor(st * 20.0) / 20.0;
  float sdf = circleSdf(vec2(0.025) + bucket, 0.025, st);

  if (sdf > 0.0)
      fragColor = vec4(BG_COLOR, 1);
  else {
    // lerp between noise changes
    float thisRandom = random(bucket + floor(iTime));
    float nextRandom = random(bucket + floor(iTime + 1.0));
    float lerp = mix(thisRandom, nextRandom, iTime - floor(iTime));
    vec3 color = mix(FG_COLOR_0, FG_COLOR_1, lerp);
    // pseudo-aa
    color = mix(color, BG_COLOR, smoothstep(-0.002, 0.0, sdf));
    fragColor = vec4(color, 1);
  }
}
