// from IQ's suite of SDFs
float sdCircle( in vec2 _st, in float _radius )
{
	return length(_st) - _radius;   
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = 4.0 * fragCoord/iResolution.x - vec2(2.0, 1.0);
    vec2 centerPT = vec2( 0.0, 1.5 );

    vec3 col = vec3(0.1);


    float circ = 0.0;

    vec2 tot = vec2( clamp( 2.25 - iTime / 1.0, 0.0, 1.0) );    
    float smiley 	= sign(sdCircle(st - tot, 0.15));

    	tot = vec2( clamp( 1.85 - iTime / 1.0, 0.0, 1.0) );
    	  smiley 	+= sign(sdCircle(st + tot, 0.17));
    col 			= mix(col, vec3(0.8, 0.8, 0.0), clamp(1.0-smiley, 0.0, 0.8));


    circ 	= sign( sdCircle(st, clamp(iTime / 5.0, 0.0, 0.25) ));
    circ 	-= sign(sdCircle(st, clamp(3.0 -iTime / 1.25, 0.0, 5.0)));  
    col 	= mix(vec3(0.0), col, clamp(circ, 0.0, 0.8) );


    fragColor = vec4( vec3(col),1.0);
}
