float sq (float x) { return x*x; }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float b = clamp(texture(iChannel0, uv).r, 0., 1.);
    vec3 col = vec3(b);
    float gl = pow(b, 10.);
    vec3 fc = vec3(
        b*b*3.-gl*0.8,
        b-gl*0.1, 
        2.*sqrt(b)*(cos(b*3.14159)+1.)*0.2+
        gl*0.8
    );
    col = fc;//vec3(b*b*3., b, sqrt(b)*(cos(b*3.14159)+1.)*0.4);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    ivec2 iuv = ivec2(fragCoord);
    vec2 mouse = iMouse.xy/iResolution.xy;

    float speed = 4.;
    vec3 past = texelFetch(iChannel0, ivec2(iuv.x, iuv.y-int(speed)), 0).rgb;
    vec3 pu = texelFetch(iChannel0, ivec2(iuv.x, iuv.y+1), 0).rgb;
    vec3 pd = texelFetch(iChannel0, ivec2(iuv.x, iuv.y-1), 0).rgb;
    vec3 pl = texelFetch(iChannel0, ivec2(iuv.x-1, iuv.y), 0).rgb;
    vec3 pr = texelFetch(iChannel0, ivec2(iuv.x+1, iuv.y), 0).rgb;


    vec3 avg = (pu+pd+pl+pr+past*2.)/6.;
    float cooldown = texture(iChannel1, vec2(uv.x, uv.y-iTime/60.*speed)).r;
    float rand = texture(iChannel1, avg.xy).g;
    vec3 col = avg-max(cooldown*rand*rand*0.4, 0.001);
    if (iTime<0.1) col = vec3(0.);
    //if (length(mouse-uv)<0.1) col = vec3(1.);
    if (iuv.y<int(speed)) col = vec3(1.);
    fragColor = vec4(col,1.0);
}
