vec2 rotate(vec2 position, float angle) {
	return mat2(cos(angle), sin(angle), -sin(angle), cos(angle)) * position;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 coord = fragCoord/iResolution.xy;

    coord -= vec2(0.5);
    coord = rotate(coord, iTime);
    coord += vec2(0.5);

    vec3 color = vec3(0.0);
    vec2 translate = vec2(-0.5, -0.5);
    coord += translate;

    for(int i = 0; i < 60; i++){
        float radius = 0.3;
        float rad = radians(360.0 / 60.0) * float(i);

        color += 0.002 / length(coord + vec2(radius * cos(rad), radius * sin(rad)));
    }

    fragColor = vec4(color,1.0);
}
