#define PARAM1 .5

// Tout ca va etre pareil pour tout les shaders
#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)

#define NeighborhoodA vec4 a_mi = A(U+vec2(0,0)),  a_n  = A(U+vec2(0,1)),  a_e  = A(U+vec2(1,0)),  a_s  = A(U-vec2(0,1)),     a_w  = A(U-vec2(1,0)),    a_ne = A(U+vec2(1,1)),      a_se = A(U+vec2(1,-1)),    a_sw = A(U+vec2(-1,-1)),  a_nw = A(U+vec2(-1,1)),    a_ave = 0.125*(a_n+a_e+a_s+a_w+a_ne+a_se+a_sw+a_nw);
#define NeighborhoodB vec4 b_mi = B(U+vec2(0,0)),  b_n  = B(U+vec2(0,1)),  b_e  = B(U+vec2(1,0)),  b_s  = B(U-vec2(0,1)),     b_w  = B(U-vec2(1,0)),    b_ne = B(U+vec2(1,1)),      b_se = B(U+vec2(1,-1)),    b_sw = B(U+vec2(-1,-1)),  b_nw = B(U+vec2(-1,1)),    b_ave = 0.125*(b_n+b_e+b_s+b_w+b_ne+b_se+b_sw+b_nw);

#define Main void mainImage (out vec4 Q, in vec2 U)

Main {

    NeighborhoodA;
    NeighborhoodB;

    a_mi.xy -= 0.25*vec2(b_e.x-b_w.x,b_n.x-b_s.x);
    a_mi.xy -= 0.25*abs(b_mi.y)*vec2(b_n.y-b_s.y,-b_e.y+b_w.y);
    a_mi -= 0.25*(
        a_n.y*a_n-a_s.y*a_s+
        a_e.x*a_e-a_w.x*a_w
                   );
    a_mi.y -= .1/R.y*(1.-a_mi.w);
    // Input 
    if (iMouse.z>0.&&length(iMouse.xy-U)<10.) {
        a_mi.y = .8;
        a_mi.z = 1.;
    }else if (length (U-vec2(.1,.9)*R) < 10.) {
    	a_mi.w = 0.;
        a_mi.xy = .2*vec2(sin(.1*iTime),-cos(.1*iTime));
    }

    // Init
    if (iFrame < 1) {
    	a_mi = vec4(0,1,1,-1)*exp(-.1*length(U-0.5*R));
        a_mi.w = 1.;
    }

    // Out
    Q = a_mi;
    if (U.x<1.||R.x-U.x<1.||R.y-U.y<1.) Q.xy *= 0.;

}

Main {

    NeighborhoodA;
    NeighborhoodB;

    b_mi.x -= 0.25*(a_n.y+a_e.x-a_s.y-a_w.x);
    b_mi.y = b_ave.y*.92+0.1*(a_n.x-a_e.y-a_s.x+a_w.y);
    b_mi -= 0.25*(
        a_n.y*b_n-a_s.y*b_s+
        a_e.x*b_e-a_w.x*b_w
                   )*vec4(0,1,1,1);

    Q = b_mi;
    if (U.x<5.||U.y<5.||R.x-U.x<5.||R.y-U.y<5.) Q.y *= 0.;

}
