// --- 257 chars

#define S(v)  smoothstep (.01, -.01, v )
void mainImage( out vec4 O,  vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y;
    float l = length(U), a = abs( atan(U.x,U.y) );
    O-=O;
    O += .4* (   S( abs(l-.8)-.02 ) * sin( -18.* a -6.3*l/.08 )
               + S( abs(l-.7)-.02 ) * sin(  18.* a -6.3*l/.08 ) 
             )* (.5+.5*sin(72.*a))
      + .5;
}

