void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u+u - R ) / R.y, 
         V = 2.*fract(4.*U) - 1.,
         T = floor(4.*U);

    float l = length(V),                             
          a = atan(V.y,V.x); a = mod(a,6.283);
    a += 10. * exp(-4.*l);     

    U += l * max(0.,cos(l*1.57))
           * vec2(cos(a),sin(a))
           * cos(iTime+14.*T.x-78.4*T.y) ; 

    O = texture(iChannel0,.5+.5*U); 
}
