void mainImage( out vec4 O, vec2 U )
{
    vec2 uv = U/iResolution.x;
    uv = abs(uv-0.3);
    vec2 u = U/17.3 + uv * .1 - iTime * 1.12345;
    float d = sin( u[int(uv.x > uv.y)]*7.2 )
              +.3 + uv.x*0.2,
          a = 0.5;
    float m = smoothstep(-a, a , d - 1.);
    float b = length(uv);
    O = vec4(m - b);
}
