const float ITER = 36.;
const float FREQ = 0.7*3.14159;

vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

//a wave with its gradient
vec4 sinC(vec2 uv, float freq, float i)
{
    vec2 ang = 2.*3.14159*hash21(i+ITER*float(iFrame/60));
 	vec2 k = vec2(sin(ang.x), cos(ang.x));
    float x = freq*dot(uv,k)  - ang.y;
    float amp = 1.;//pow(freq, -3.5);
    float H = amp*sin(x);
    float Hdx = amp*cos(x);
    return vec4(H, Hdx*k*freq, 0.3*amp);
}

vec3 map(vec2 uv) {
    float freq = FREQ;
    float amp = 1.;

    vec4 h = vec4(0.0);
    for(float i = 0.; i < ITER; i++) 
    {        
        h += sinC(uv, freq, i);
    	freq *= 1.0 + 0.3/ITER;
    }
    return 0.5*h.xyz/h.w + 0.5;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //pixel coordinates
    vec2 p = 0.4*fragCoord;

    vec3 wave = map(p);

    if(iMouse.z > 0.)
    {
    	if(iMouse.x > fragCoord.x) 
       		 wave = texture(iChannel0, p/1024.).xyz;
    }
    else
        if(fragCoord.x < 0.5*iResolution.x) 
       		 wave = texture(iChannel0, p/1024.).xyz;


    fragColor = vec4(wave.xxx,1.0);
}
