#define PASOS 128
#define EPSILON 0.001
#define MAXIMO 20.0
#define PI 3.1415

mat2 rot(float a){
    return mat2(
        +cos(a), -sin(a), 
        +sin(a), +cos(a)
    );
}
// Proyección sobre plano
vec3 proyPlano(vec3 p, vec3 n){
    return p - dot(p, n) * n;
}
vec3 proy01(in vec3 a, in vec3 b){
    return b * clamp(dot(b, a) / dot(b, b), 0., 1.);
}

// FDS circunsferencia
float SDFCircunferencia(vec2 p, float r){
	return length(p) - r;
}
// FDS Cilindro Finito (Con Tapa)
float SDFCilindro(vec3 p, float h, float r){
    // Proyeción sobre el plano YZ => n = X
    vec3 n = normalize(vec3(1, 0, 0));
    // Calculamos la proyección
    vec3 proy = proyPlano(p, n);
    // Altura sobre la tapa.
    float dc = length(p - proy) - h;
    // FDS Circunferencia de radio r sobre la proyección en el plano YZ.
    float fproy = SDFCircunferencia(proy.yz, r);
    // Exterior figura
    float dint = length(max(vec2(dc,fproy),0.));
    // Interior de la figura
    float dext = min(max(dc, fproy), 0.);
    // Sumamos ambos
    return dint + dext;
}

float escena_sdf(vec3 p){
	// Cilindro
    return SDFCilindro(p, 0.3, 0.2);
}

// Homomorfismo
float h3(float h){
    return pow(h, pow(2., 3.));
}

// Producto Escalar normalizado positivo.
float dot01(vec3 a, vec3 b){ 
    return max(dot(a,b)/(length(a)*length(b)), 0.0);
}

float f_difusa(float distancia){
	return 1.0;
}

float SphereMarching(in vec3 ojo, in vec3 direccion, float distancia_plano){
    float distancia = 0.0;
    // Realizamos PASOS iteraciones de marching.
    for(int i = 0; i < PASOS; ++i){
        // Calculamos la cabeza del rayo
        vec3 rayo = ojo + direccion * distancia;
        // Calculamos el radio de la esfera más próxima a la isosuperficie
        float radio = escena_sdf(rayo);
        // Si el radio (distancia mínima a la isosuperficie es muy pequeña, estamos devolvemos la distancia del vector final. Esta distancia la utilizaremos más adelante para calcular el vector que apunta a la isosuperficie.
        if(radio < EPSILON){
            return distancia;
        }
        // Incrementamos la distancia recorrida si no estamos cerca de la isosuperficie.
        distancia += radio;
        // Comprobamos que no se haya superado la distancia de dibujado máximo. Para aquellos texels cuyo valor sea -1.0. Podemos considerarlos, el fondo de la escena.
        if(distancia > distancia_plano) break;
    }
    return distancia_plano;
}

// Cálculo de la normal de la isosuperficie intersectada por un rayo que ha sido estimado.
vec3 Normal(vec3 p){
     // f(x1,...,xn)
     float fxyz = escena_sdf(p);
     // f(x1,..,xi+h,xn)
     float fxhyz = escena_sdf(p + vec3(EPSILON, 0.0, 0.0));
     float fxyhz = escena_sdf(p + vec3(0.0, EPSILON, 0.0));
     float fxyzh = escena_sdf(p + vec3(0.0, 0.0, EPSILON));

     // Utilizamos la definicion de derivadas parciales para devolver el gradiente, que se trata de la normal de la isosuperficie.
     return vec3(
         (fxhyz - fxyz) / EPSILON,
         (fxyhz - fxyz) / EPSILON,
         (fxyzh - fxyz) / EPSILON
     );
}

// Modelo de iluminación Phong con sombras duras
float ModeloIluminacion(vec3 direccion, vec3 p){
    // Calculate normal of the point.
    vec3 normal = Normal(p);
    float intensidad = 0.0;
    // Intensidad Ambiente Global
    intensidad += 0.2;
    // Punto p de la isosuperficie, separado del suelo para ayuda a acelerar el cálculo de 
    p = p + normal * 0.1;
    // Intensidad de cada Luz
    // Luz 1.
    vec3 posicion_luz_1 = vec3(3.0, 3.0, 1.);
    vec3 d_luz_1 = posicion_luz_1 - p;
    vec3 dir_luz_1 = normalize(d_luz_1);
    float dst_luz_1 = length(d_luz_1);
    // En el caso de que se trate de una luz direccional, utilizaremos el plano MAXIMO, utilizado antes.
    if(SphereMarching(p, dir_luz_1, dst_luz_1) >= dst_luz_1){
        // Intensidad Difusa
        intensidad += dot01(d_luz_1, normal);
        // Intensidad Especular (Si no es direccional)
        vec3 r_luz_1 = reflect(d_luz_1, normal);
        intensidad += f_difusa(dst_luz_1) * h3(dot01(r_luz_1, direccion));
    }

    // Luz 2. Direccional
    vec3 dir_luz_2 = -normalize(vec3(1., 0., 0.));
    // En el caso de que se trate de una luz direccional, utilizaremos el plano MAXIMO, utilizado antes.
    if(SphereMarching(p, dir_luz_2, MAXIMO) >= MAXIMO){
        // Intensidad Difusa
        intensidad += dot01(dir_luz_2, normal);
    }

    return clamp(intensidad, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    // Normalizamos las coordendas y las reescalamos para mantener el ratio de aspecto. Transladamos al centro de la pantalla.
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / min(iResolution.y, iResolution.x);
    // Definimos el ojo y la pantalla, que se encuentra en nuestra escena. Este debe estar detrás de la pantalla ya que de este se “lanzarán” los rayos que pasarán por la pantalla.
    vec3 ojo = vec3(0.0, 0.0, -1.0);
    vec3 pantalla = vec3(uv, 0.0);
    // Es fácil observar que la dirección del rayo es el vector normalizado que apunta desde el ojo hasta la pantalla
    vec3 direccion = normalize(pantalla - ojo);
    // Con esto, ya podemos utilizar nuestro Sphere marcher.
    float distancia = SphereMarching(ojo, direccion, MAXIMO);
    if(distancia < MAXIMO){
        // Estamos "sobre" la isosuperficie. 
        // El punto será el formado por el ojo y en dirección modulada con la distancia acumulada.
        vec3 p = ojo + direccion * distancia;
        // Con la información actual, podemos aplicar un modelo de iluminación.
        float intensidad = ModeloIluminacion(direccion, p);
        // Utilizamos el color blanco para dibujar la isosuperficie.
        fragColor = vec4(1.0) * intensidad;
    }else{
        // Estamos en el fondo de la escena.

        // Utilizamos el color negro para dibujar el fondo.
        fragColor = vec4(vec3(0.0), 1.0);
    }
}
